import { useState } from "react";
import "./App.css";

function App() {
  const [username, setUsername] = useState("Paco Rabany");
  const [password, setPassword] = useState("");
  const [bio, setBio] = useState("");
  const [gender, setGender] = useState("m");
  const [userType, setUserType] = useState("individual");

  return (
    <div className="App">
      <header className="App-header">
        <form>
          <div>
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              name="username"
              onChange={(e) => setUsername(e.target.value)}
              value={username}
            />
          </div>

          <div>
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              onChange={(e) => setPassword(e.target.value)}
              value={password}
            />
          </div>

          <div>
            <label htmlFor="bio">Bio:</label>
            <input
              type="textarea"
              id="bio"
              name="bio"
              onChange={(e) => setBio(e.target.value)}
              value={bio}
            />
          </div>

          <div>
            <label htmlFor="gender">Gender:</label>
            <select
              name="gender"
              id="gender"
              onChange={(e) => setGender(e.target.value)}
              value={gender}
            >
              <option value="m">M</option>
              <option value="f">F</option>
            </select>
          </div>

          <div>
            <label htmlFor="individual-user-type">Individual: </label>
            <input
              type="radio"
              name="userType"
              value="individual"
              id="individual-user-type"
              onChange={(e) => setUserType(e.target.value)}
              checked={userType === "individual"}
            />
          </div>

          <div>
            <label htmlFor="corporate-user-type">Corporate: </label>
            <input
              type="radio"
              name="userType"
              value="corporate"
              id="corporate-user-type"
              onChange={(e) => setUserType(e.target.value)}
              checked={userType === "corporate"}
            />
          </div>
        </form>
      </header>
    </div>
  );
}

export default App;
