import { useState } from "react";
import "./App.css";

function App() {
  const [values, setValues] = useState({
    username: "",
    password: "",
    bio: "",
    gender: "m",
    userType: "individual",
    tac: true,
  });

  const [password, setPassword] = useState("");
  const [bio, setBio] = useState("");
  const [gender, setGender] = useState("m");
  const [userType, setUserType] = useState("individual");
  const [tac, setTac] = useState(false);

  const changeHandler = (e) => {
    setValues((state) => ({
      ...state,
      [e.target.name]: e.target.value,
    }));
  };

  const submitHandler = (e) => {
    e.preventDefault();

    console.log(e.target.values);
  };

  return (
    <div className="App">
      <header className="App-header">
        <form onSubmit={submitHandler}>
          <div>
            <label htmlFor="username">Username:</label>
            <input
              type="text"
              id="username"
              name="username"
              onChange={changeHandler}
              value={values.username}
            />
          </div>

          <div>
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              onChange={(e) => setPassword(e.target.value)}
              value={password}
            />
          </div>

          <div>
            <label htmlFor="bio">Bio:</label>
            <textarea
              name="bio"
              id="bio"
              cols="30"
              rows="10"
              onChange={(e) => setBio(e.target.value)}
              value={bio}
            />
          </div>

          <div>
            <label htmlFor="gender">Gender:</label>
            <select
              name="gender"
              id="gender"
              onChange={(e) => setGender(e.target.value)}
              value={gender}
            >
              <option value="m">M</option>
              <option value="f">F</option>
            </select>
          </div>

          <div>
            <label htmlFor="individual-user-type">Individual: </label>
            <input
              type="radio"
              name="userType"
              value="individual"
              id="individual-user-type"
              onChange={(e) => setUserType(e.target.value)}
              checked={userType === "individual"}
            />
          </div>

          <div>
            <label htmlFor="corporate-user-type">Corporate: </label>
            <input
              type="radio"
              name="userType"
              value="corporate"
              id="corporate-user-type"
              onChange={(e) => setUserType(e.target.value)}
              checked={userType === "corporate"}
            />
          </div>

          <div>
            <label htmlFor="tac">Terms and Conditions: </label>
            <input
              type="checkbox"
              name="tac"
              id="tac"
              onChange={() => setTac(!tac)}
              value={tac}
            />
          </div>

          <input type="submit" value="Submit" />
        </form>
      </header>
    </div>
  );
}

export default App;
