export const minLength = (e, bound, setErrors) => {
  setErrors((state) => ({
    ...state,
    [e.target.name]: e.target.value.length < bound,
  }));
};

export const emailValidation = (e, email, setErrors) => {
  const pattern = /@.{3,}/gm;

  const result = email.match(pattern);

  setErrors((state) => ({
    ...state,
    [e.target.name]: result === null,
  }));
};
